<script>
// 网络请求
import { getGoodsByUIdAndState } from "api/goods";

// utils
import { intercept } from "utils/time";

export default {
  data() {
    return {
      state: 0,
      goods: {},
      total: 0,
      soldTotal: 0,
    };
  },
  created() {
    this.getGoods();
  },
  methods: {
    isActive(i) {
      return { "profile-goods-top-group-active": i == this.state };
    },
    changeState(state) {
      this.state = state;
      this.getGoods();
    },
    getGoods() {
      getGoodsByUIdAndState(
        JSON.parse(sessionStorage.getItem("user")).id,
        this.state
      ).then((res) => {
        if (res.status == 200) this.goods = res.data;
        else if (res.status == 201) this.goods = {};
        console.log(this.goods);
      });
    },
    getTime(time) {
      return intercept(time).substr(0, 7);
    },
    toDetail(id) {
      this.$router.push(`/GoodsDetail/${id}`);
    },
  },
};
</script>

