import { getUser } from "api/user";

export default {
  data() {
    return {
      user: null,
    };
  },
  created() {
    getUser(JSON.parse(sessionStorage.getItem("user")).id).then((res) => {
      if (res.status == 200) this.user = res.data;
    });
  },
};
</script>

