<script>
// 子组件

// 网络请求
import { getAll } from "api/province.js";
import { getCityByProvinceId } from "api/city.js";
import { getSchoolByCityId, addApply } from "api/school.js";
import { sendCode, checkEmail, register } from "api/user.js";

// utils
import {
  verifyEmail,
  verifyUsername,
  verifyPassword,
  verifyNickname,
} from "utils/regular.js";

export default {
  name: "Register",
  components: {},
  data() {
    return {
      message: "", // 注册提示信息
      tipIsShow: false,
      emailMsg: "点击发送验证码至邮箱",
      isSendCode: true, // 是否能够发送邮件
      user: {
        username: "", // 用户名
        nickname: "", // 昵称
        sex: "", // 性别
        password: "", // 密码
        email: "", // 邮箱
        school: 0, // 学校
      },
      province: 0, // 省份
      city: 0, // 城市
      confirmPassword: "", // 确定密码
      verificationCode: "", // 验证码
      correctCode: "", // 正确的验证码
      provinceList: [], // 省份列表
      cityList: [], // 城市列表
      schoolList: [], // 学校列表
      addProvince: 0, // 添加学校所对应的省份id
      addCity: 0, // 添加学校所对应的城市id
      addSchool: "", // 添加学校的名称
      addCityList: [], // 添加学校所对的所有城市
      addSchoolIsShow: false,
    };
  },
  methods: {
    /**
     * 注册
     */
    toRegister() {
      const flag = this.checkUser();
      if (flag) {
        register(this.user).then((res) => {
          if (res.status == 200) {
            this.tipMsg("注册成功！");
            this.$router.push("/login");
          }
          else {
            this.tipMsg(res.message);
            Object.keys(this.user).forEach((key) => (this.user[key] = ""));
          }
        });
      }
    },
    /**
     * 发送验证码
     */
    code() {
      if (!verifyEmail(this.user.email)) {
        this.tipMsg("邮箱格式不正确！请重新填写！");
        this.user.email = "";
        return;
      }
      checkEmail(this.user.email).then((res) => {
        if (res.status != 200) {
          this.tipMsg("该邮箱已被注册");
          this.user.email = "";
          return;
        } else {
          if (this.isSendCode) {
            let count = 60; // 倒计时
            let timer = null; // 计时器
            sendCode([this.user.email]).then((r) => {
              if (r.status == 200) {
                this.correctCode = r.data.code;
                this.isSendCode = false;
                timer = setInterval(() => {
                  if (count <= 0) {
                    clearInterval(timer);
                    this.isSendCode = true;
                    this.emailMsg = "点击发送验证码至邮箱";
                  }
                  this.emailMsg = `请在${count}秒后再次发送`;
                  count--;
                }, 1000);
              } else this.tipMsg("发送验证码失败！");
            });
          }
        }
      });
    },
    /**
     * 验证邮箱
     */
    checkMail() {
      if (!verifyEmail(this.user.email)) {
        this.tipMsg("邮箱格式不正确！请重新填写！");
        this.user.email = "";
        return false;
      }
      checkEmail(this.user.email).then((res) => {
        if (res.status != 200) {
          this.tipMsg("该邮箱已被注册");
          this.user.email = "";
          return false;
        } else return true;
      });
    },
    /**
     * 验证用户信息
     */
    checkUser() {
      if (!this.user.username) {
        this.tipMsg("用户名未填写！");
        return false;
      } else if (!verifyUsername(this.user.username)) {
        this.tipMsg("用户名格式不正确！");
        return false;
      }
      if (!this.user.nickname) {
        this.tipMsg("昵称格式未填写！");
        return false;
      } else if (!verifyNickname(this.user.nickname)) {
        this.tipMsg("昵称格式不正确！");
        return false;
      }
      if (!this.user.sex) {
        this.tipMsg("请选择性别！");
        return false;
      }
      if (!this.user.password) {
        this.tipMsg("密码未填写！");
        return false;
      } else if (!verifyPassword(this.user.password)) {
        this.tipMsg("密码格式不正确！");
        return false;
      }
      if (!this.confirmPassword || "" == this.confirmPassword) {
        this.tipMsg("确定密码未填写！");
        return false;
      } else if (this.confirmPassword != this.user.password) {
        this.tipMsg("输入的两次密码不一致！");
        return false;
      }
      if (!this.user.email) {
        this.tipMsg("请填写邮箱！");
        return false;
      } else if (!verifyEmail(this.user.email)) {
        this.tipMsg("邮箱格式不正确！请重新填写！");
        return false;
      } else if (this.correctCode != this.verificationCode) {
        this.tipMsg("验证码错误！");
        return false;
      }
      if (!this.province) {
        this.tipMsg("请选择省份！");
        return false;
      }
      if (!this.city) {
        this.tipMsg("请选择城市！");
        return false;
      }
      if (!this.user.school) {
        this.tipMsg("请选择学校！");
        return false;
      }
      return true;
    },
    /**
     * 显示提示信息
     * @param {String} Str 提示信息
     */
    tipMsg(str) {
      this.message = str;
      this.tipIsShow = true;
    },
    /**
     * 添加学校申请
     */
    toAddSchool() {
      if (!this.addProvince) {
        this.tipMsg("请选择省份！");
        return;
      }
      if (!this.addCity) {
        this.tipMsg("请选择城市！");
        return;
      }
      if (!this.addSchool) {
        this.tipMsg("请填写学校名称");
        return;
      }
      addApply({
        pId: this.addProvince,
        cId: this.addCity,
        sName: this.addSchool,
      }).then((res) => {
        if (res.status == 200) {
          this.tipMsg("提交申请成功！");
          this.addSchoolIsShow = false;
        } else this.tipMsg("提交申请失败！");
      });
    },
  },
  created() {
    getAll().then((res) => {
      this.provinceList = res.data;
    });
  },
  watch: {
    province() {
      getCityByProvinceId(this.province).then((res) => {
        if (res.data) this.cityList = res.data;
      });
    },
    city() {
      getSchoolByCityId(this.city).then((res) => {
        if (res.data) this.schoolList = res.data;
      });
    },
    addProvince() {
      getCityByProvinceId(this.addProvince).then((res) => {
        if (res.data) this.addCityList = res.data;
      });
    },
  },
};
</script>

